import React, { useState } from 'react';
import { supabase } from './supabaseClient';

function AuthForm({ onLogin }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isSigningUp, setIsSigningUp] = useState(false);

  const handleSignup = async () => {
    const { error } = await supabase.auth.signUp({
      email,
      password,
    });
    if (error) alert("Signup error: " + error.message);
    else alert("Signup successful! Check your email to confirm.");
  };

  const handleLogin = async () => {
    const { error, data } = await supabase.auth.signInWithPassword({
      email,
      password,
    });
    if (error) alert("Login error: " + error.message);
    else onLogin(data.session.user); // Call parent handler
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial", maxWidth: 400, margin: "auto" }}>
      <h1>{isSigningUp ? "Sign Up" : "Login"}</h1>
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        style={{ width: "100%", marginBottom: "0.5rem", padding: "0.5rem" }}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        style={{ width: "100%", marginBottom: "0.5rem", padding: "0.5rem" }}
      />
      {isSigningUp ? (
        <>
          <button onClick={handleSignup} style={{ width: "100%", padding: "0.5rem" }}>
            Sign Up
          </button>
          <p>
            Already have an account?{" "}
            <button onClick={() => setIsSigningUp(false)} style={{ color: "blue", background: "none", border: "none" }}>
              Login
            </button>
          </p>
        </>
      ) : (
        <>
          <button onClick={handleLogin} style={{ width: "100%", padding: "0.5rem" }}>
            Login
          </button>
          <p>
            Don't have an account?{" "}
            <button onClick={() => setIsSigningUp(true)} style={{ color: "blue", background: "none", border: "none" }}>
              Sign Up
            </button>
          </p>
        </>
      )}
    </div>
  );
}

export default AuthForm;
