import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';


import React, { useState, useEffect } from 'react';
import { supabase } from './supabaseClient';

function App() {
  const [trades, setTrades] = useState([]);
  const [form, setForm] = useState({
    symbol: '',
    quantity: '',
    price: '',
    type: 'buy',
  });

  // Fetch trades from Supabase
  useEffect(() => {
    fetchTrades();
  }, []);

  const fetchTrades = async () => {
    const { data, error } = await supabase.from('trades').select('*').order('created_at', { ascending: true });
    if (error) {
      console.error('Error fetching trades:', error.message);
    } else {
      setTrades(data);
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { data, error } = await supabase.from('trades').insert([form]);
    if (error) {
      alert('Error saving trade: ' + error.message);
    } else {
      setForm({ symbol: '', quantity: '', price: '', type: 'buy' });
      fetchTrades(); // Refresh list
    }
  };
function calculatePnL(trades) {
  const summary = {};

  trades.forEach((trade) => {
    const { symbol, quantity, price, type } = trade;
    if (!summary[symbol]) {
      summary[symbol] = {
        totalBuyQty: 0,
        totalBuyCost: 0,
        totalSellQty: 0,
        totalSellRevenue: 0,
      };
    }

    if (type === 'buy') {
      summary[symbol].totalBuyQty += Number(quantity);
      summary[symbol].totalBuyCost += Number(quantity) * Number(price);
    } else if (type === 'sell') {
      summary[symbol].totalSellQty += Number(quantity);
      summary[symbol].totalSellRevenue += Number(quantity) * Number(price);
    }
  });

  const pnl = Object.entries(summary).map(([symbol, data]) => {
    const netQty = data.totalBuyQty - data.totalSellQty;
    const avgBuyPrice = data.totalBuyQty > 0 ? data.totalBuyCost / data.totalBuyQty : 0;
    const profit = data.totalSellRevenue - (avgBuyPrice * data.totalSellQty);

    return {
    symbol,
    netQty,
    avgBuyPrice: Number(avgBuyPrice.toFixed(2)),
    profit: Number(profit.toFixed(2)),
  };

  });

  return pnl;
}

  return (
    <div style={{ padding: '2rem', fontFamily: 'Arial' }}>
      <h1>📈 Paper Trading App</h1>

      <form onSubmit={handleSubmit} style={{ marginBottom: '2rem' }}>
        <input
          type="text"
          name="symbol"
          placeholder="Stock Symbol (e.g., AAPL)"
          value={form.symbol}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="quantity"
          placeholder="Quantity"
          value={form.quantity}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="price"
          placeholder="Price"
          value={form.price}
          onChange={handleChange}
          required
        />
        <select name="type" value={form.type} onChange={handleChange}>
          <option value="buy">Buy</option>
          <option value="sell">Sell</option>
        </select>
        <button type="submit">Add Trade</button>
      </form>

      <h2>Trade History</h2>
      <table border="1" cellPadding="8">
        <thead>
          <tr>
            <th>Symbol</th>
            <th>Qty</th>
            <th>Price</th>
            <th>Type</th>
            <th>Time</th>
          </tr>
        </thead>
        <tbody>
          {trades.map((trade) => (
            <tr key={trade.id}>
              <td>{trade.symbol}</td>
              <td>{trade.quantity}</td>
              <td>{trade.price}</td>
              <td>{trade.type}</td>
              <td>{new Date(trade.created_at).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Profit & Loss Summary</h2>
<table border="1" cellPadding="8">
  <thead>
    <tr>
      <th>Symbol</th>
      <th>Net Quantity</th>
      <th>Avg Buy Price</th>
      <th>P&L (₹)</th>
    </tr>
  </thead>
  <tbody>
    {calculatePnL(trades).map((row) => (
      <tr key={row.symbol}>
        <td>{row.symbol}</td>
        <td>{row.netQty}</td>
        <td>{row.avgBuyPrice}</td>
        <td style={{ color: row.profit >= 0 ? 'green' : 'red' }}>
          ₹{row.profit}
        </td>
      </tr>
    ))}
  </tbody>
</table>
<h2>Visual P&L Chart</h2>
<ResponsiveContainer width="100%" height={300}>
  <BarChart data={calculatePnL(trades)} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
    <CartesianGrid strokeDasharray="3 3" />
    <XAxis dataKey="symbol" />
    <YAxis />
    <Tooltip />
    <Legend />
    <Bar dataKey="profit" fill="#82ca9d" />
  </BarChart>
</ResponsiveContainer>


    </div>
  );
}

export default App;
