import React, { useState } from "react";
import { supabase } from "./supabaseClient";

function AuthForm({ onLogin }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isSignUp, setIsSignUp] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    let response;
    if (isSignUp) {
      response = await supabase.auth.signUp({ email, password });
      // On sign-up, user might need email confirmation depending on your Supabase settings.
      if (response.error) setError(response.error.message);
      else alert("Check your email for confirmation link (if enabled)");
    } else {
      response = await supabase.auth.signInWithPassword({ email, password });
      if (response.error) setError(response.error.message);
      else if (response.data.user) {
        onLogin(response.data.user);
      }
    }

    setLoading(false);
  };

  return (
    <div style={{ maxWidth: 400, margin: "auto", padding: 20, fontFamily: "Arial" }}>
      <h2>{isSignUp ? "Sign Up" : "Login"}</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          style={{ width: "100%", padding: 8, marginBottom: 10 }}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          style={{ width: "100%", padding: 8, marginBottom: 10 }}
        />
        <button type="submit" disabled={loading} style={{ width: "100%", padding: 10 }}>
          {loading ? "Please wait..." : isSignUp ? "Sign Up" : "Login"}
        </button>
      </form>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <p style={{ marginTop: 15 }}>
        {isSignUp ? "Already have an account?" : "Don't have an account?"}{" "}
        <button
          onClick={() => {
            setError(null);
            setIsSignUp(!isSignUp);
          }}
          style={{ color: "blue", cursor: "pointer", background: "none", border: "none", padding: 0 }}
        >
          {isSignUp ? "Login" : "Sign Up"}
        </button>
      </p>
    </div>
  );
}

export default AuthForm;
