// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { supabase } from './supabaseClient';
import { TradingDataProvider } from './TradingDataContext';

// Import your components
import Auth from './components/Auth.jsx';
import Account from './pages/Account.js';
import Dashboard from './components/Dashboard.js'; // Assuming Dashboard is a component now
import Navbar from './components/Navbar.js';
import StockDetailPage from './pages/StockDetailsPage.js';
import AboutDeveloper from './pages/AboutDeveloper.js'; // <--- ADD THIS LINE

const App = () => {
    const [session, setSession] = useState(null);
    const [loadingSession, setLoadingSession] = useState(true);

    useEffect(() => {
        supabase.auth.getSession().then(({ data: { session } }) => {
            setSession(session);
            setLoadingSession(false);
        });

        const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
            setSession(session);
        });

        return () => subscription.unsubscribe();
    }, []);

    if (loadingSession) {
        return <div>Loading...</div>; // Or a more elaborate loading spinner
    }

    return (
        <Router>
            {/* Navbar should always be visible regardless of authentication */}
            <Navbar session={session} /> 
            <TradingDataProvider session={session}>
                <Routes>
                    <Route path="/" element={!session ? <Auth /> : <Navigate to="/dashboard" replace />} />
                    <Route path="/dashboard" element={session ? <Dashboard /> : <Navigate to="/" replace />} />
                    <Route path="/account" element={session ? <Account /> : <Navigate to="/" replace />} />
                    <Route path="/stock/:symbol" element={session ? <StockDetailPage /> : <Navigate to="/" replace />} />
                    {/* <Route path="/watchlist" element={session ? <Watchlist /> : <Navigate to="/" replace />} /> */
                    /* Assuming a Watchlist component exists and is imported if needed */
                    }
                    <Route path="/about-developer" element={<AboutDeveloper />} /> {/* <--- ADD THIS ROUTE */}
                </Routes>
            </TradingDataProvider>
        </Router>
    );
};

export default App;
