import React, { useState, useEffect } from "react";
import axios from "axios";
import { supabase } from "./supabaseClient"; // Ensure this path is correct
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  Cell,
} from "recharts";

// IMPORTANT: Replace with your actual Finnhub API Key from finnhub.io
const FINNHUB_API_KEY = "d0uvgepr01qmg3uj9ug0d0uvgepr01qmg3uj9ugg"; // <--- REPLACE THIS!!!

// Helper to check for placeholder or dummy API keys
const isInvalidApiKey = (key) => {
  return !key ||
         key === "YOUR_FINNHUB_API_KEY" || // Generic placeholder
         key === "d0uv0tpr01qmg3uj77qgd0uv0tpr01qmg3uj77r0" || // Common dummy key
         key === "d0uvgepr01qmg3uj9ug0d0uvgepr01qmg3uj9ugg";   // Placeholder in THIS file
};

const CURRENCY_SYMBOL = "$";

function TradingDashboard({ user }) { // user prop comes from App.js
  // Log the received user prop for debugging
  useEffect(() => {
    console.log("TradingDashboard mounted or user prop changed. User:", JSON.stringify(user, null, 2));
  }, [user]);

  const [trades, setTrades] = useState([]);
  const [livePrices, setLivePrices] = useState({});
  const [form, setForm] = useState({ symbol: "", quantity: "", type: "buy" });
  const [capital, setCapital] = useState(() => {
    // Ensure user and user.id exist before trying to access localStorage
    const userId = user?.id;
    if (userId) {
      const saved = localStorage.getItem("capital_" + userId);
      return saved ? Number(saved) : 100000; // Default capital in USD
    }
    return 100000; // Default if no user.id (should ideally not happen if App.js guards this)
  });
  const [availableSymbols, setAvailableSymbols] = useState([]);
  const [symbolError, setSymbolError] = useState("");

  useEffect(() => {
    const fetchAvailableSymbols = async () => {
      if (isInvalidApiKey(FINNHUB_API_KEY)) {
        console.error("Cannot fetch symbols: Invalid Finnhub API Key detected.");
        setSymbolError(
          "Could not fetch available symbols. Please set a valid Finnhub API Key in the code."
        );
        setAvailableSymbols([]);
        return;
      }
      try {
        console.log("Fetching available US market symbols from Finnhub...");
        const res = await axios.get(
          `https://finnhub.io/api/v1/stock/symbol?exchange=US&token=${FINNHUB_API_KEY}`
        );
        if (res.data && Array.isArray(res.data)) {
          const symbols = res.data
            .filter(
              (s) =>
                s.type === "Common Stock" &&
                s.displaySymbol &&
                s.symbol
            )
            .map((s) => s.displaySymbol.toUpperCase());
          setAvailableSymbols(symbols);
          console.log(`Fetched ${symbols.length} US market symbols.`);
          if (symbols.length === 0) {
            setSymbolError("No US market symbols found. Check API key permissions or Finnhub US data coverage.");
          } else {
            setSymbolError(""); 
          }
        } else {
          console.warn("No symbols returned or unexpected format from Finnhub:", res.data);
          setSymbolError("Could not fetch symbols: No data or unexpected format from API for US market.");
          setAvailableSymbols([]);
        }
      } catch (err) {
        console.error("Error fetching available US symbols:", err.message, err.response?.data);
        setSymbolError(
          `Could not fetch available US symbols: ${err.message}. Check API key, network, and API limits.`
        );
        setAvailableSymbols([]);
      }
    };
    fetchAvailableSymbols();
  }, []); // Runs once on mount

  useEffect(() => {
    // Fetch trades only if user and user.id are valid
    if (user && user.id && typeof user.id === 'string' && !user.id.startsWith("dummy-user-id")) {
        fetchTrades();
    } else if (user && user.id) { // Log if user.id is present but potentially problematic
        console.warn("fetchTrades skipped: user.id might be a placeholder:", user.id);
    }
  }, [user]); 

  useEffect(() => {
    if (isInvalidApiKey(FINNHUB_API_KEY)) {
      if (!symbolError.includes("API Key")) {
        setSymbolError("Live price updates paused: Please set a valid Finnhub API Key.");
      }
      return;
    }

    const symbolsFromTrades = trades.map((t) => t.symbol.toUpperCase());
    const symbolFromForm = form.symbol ? [form.symbol.toUpperCase()] : [];

    const uniqueSymbols = [...new Set([...symbolsFromTrades, ...symbolFromForm])];
    const symbolsToFetch = uniqueSymbols.filter((s) => !!s); 

    if (symbolsToFetch.length === 0) {
        return;
    }

    const fetchPrices = async () => {
      // console.log("Fetching prices for US symbols:", symbolsToFetch); // Can be noisy
      const currentPricesBatch = {};
      // let anErrorOccurred = false; // Error handling for batch fetches can be complex

      for (const sym of symbolsToFetch) {
        try {
          const res = await axios.get(
            `https://finnhub.io/api/v1/quote?symbol=${sym}&token=${FINNHUB_API_KEY}`
          );
          if (res.data && typeof res.data.c === 'number' && res.data.c !== 0) { 
            currentPricesBatch[sym] = res.data.c;
          } else {
            // console.warn(`No valid price data for US symbol ${sym}. Finnhub response:`, res.data);
          }
        } catch (err) {
          // console.error("Error fetching price for US symbol", sym, err.message);
          // if(!anErrorOccurred) { 
          //   anErrorOccurred = true;
          // }
        }
      }

      if (Object.keys(currentPricesBatch).length > 0) {
        setLivePrices((prevPrices) => ({ ...prevPrices, ...currentPricesBatch }));
      }
    };

    fetchPrices();
    const interval = setInterval(fetchPrices, 15000);
    return () => clearInterval(interval);
  }, [trades, form.symbol]); // Dependencies for fetching prices

  useEffect(() => {
    // Save capital to localStorage only if user and user.id are valid
    if (user && user.id && typeof user.id === 'string' && !user.id.startsWith("dummy-user-id")) {
        localStorage.setItem("capital_" + user.id, capital.toString());
    }
  }, [capital, user]);

  const fetchTrades = async () => {
    // This function is now called by the useEffect above, which already checks user and user.id
    // if (!user || !user.id) return; // Redundant check if useEffect handles it, but safe
    const { data, error } = await supabase
      .from("trades")
      .select("*")
      .eq("user_id", user.id) // Uses the user.id from props
      .order("created_at", { ascending: true });

    if (!error) setTrades(data);
    else console.error("Error fetching trades:", error.message);
  };

  const handleChange = (e) => {
    let value = e.target.value.toUpperCase();
    value = value.replace(/[^A-Z0-9]/g, ''); 
    setForm({ ...form, [e.target.name]: value });
  };

  const validateAndExecuteTrade = async (type) => {
    // **Explicit check for valid user and user.id at the start of trade execution**
    if (!user || !user.id || typeof user.id !== 'string' || !user.id.includes('-') || user.id.startsWith("dummy-user-id")) {
      // The check `!user.id.includes('-')` is a loose check for UUID. A more robust regex could be used.
      alert("User Authentication Error: Cannot execute trade without a valid user session. Please ensure you are logged in correctly.");
      console.error("Trade rejected: Invalid user object or user ID. User ID:", user ? user.id : "N/A");
      return;
    }
    console.log(`Attempting ${type} trade for user_id: ${user.id} with symbol: ${form.symbol}`); // Log before execution


    if (isInvalidApiKey(FINNHUB_API_KEY)) {
      alert("Trading disabled: Invalid Finnhub API Key. Please update it in the code.");
      return;
    }

    const sym = form.symbol.toUpperCase(); 
    const qty = Number(form.quantity);

    if (!sym || !qty || qty <= 0) {
      alert("Invalid input: Symbol and positive quantity are required.");
      return;
    }

    if (availableSymbols.length > 0 && !availableSymbols.includes(sym)) {
      alert(
        `Symbol ${sym} is not in the recognized list of US stocks. ` +
        `Please select from suggestions or ensure it's a valid US ticker.`
      );
      setSymbolError(`Symbol ${sym} not found in available US stock list. Is your API key working?`);
      return;
    }
    // This warning can be noisy if availableSymbols takes time to load, consider its placement or condition
    // if (availableSymbols.length === 0 && !symbolError.includes("API Key") && !symbolError.includes("Could not fetch")){
    //     console.warn(`Available US symbols list is empty or still loading. Attempting trade for ${sym} without list verification.`);
    // }

    const price = livePrices[sym];
    if (typeof price !== 'number' || price <= 0) { 
      alert(
        `Live price for ${sym} is currently unavailable or invalid. ` +
        `Ensure the symbol is a correct US ticker, actively traded, and your API key is working. Try again shortly.`
      );
      setSymbolError(`No valid live price for ${sym}. Check US ticker, API data availability, or API key.`);
      return;
    }

    // Prepare the trade object with the (now hopefully valid) user.id
    const trade = { user_id: user.id, symbol: sym, quantity: qty, price, type };

    if (type === "buy") {
      const cost = qty * price;
      if (cost > capital) {
        alert("Not enough capital to perform this buy trade.");
        return;
      }
      // Optimistically update capital, will be reverted on error
      setCapital((c) => c - cost); 
    } else { // Sell
      const pnlSummary = calculatePnL(trades); 
      const heldStock = pnlSummary.find(s => s.symbol === sym);
      if (!heldStock || heldStock.netQty < qty) {
          alert(`You only hold ${heldStock ? heldStock.netQty : 0} of ${sym}. Cannot sell ${qty}.`);
          return;
      }
      // Optimistically update capital
      setCapital((c) => c + qty * price);
    }

    const { error } = await supabase.from("trades").insert([trade]);

    if (error) {
      alert("Error executing trade with database: " + error.message);
      // Revert optimistic capital update if database insert fails
      if (type === "buy") {
        setCapital((c) => c + (qty * price)); // Add back the cost
      } else { // Sell
        setCapital((c) => c - (qty * price)); // Subtract back the proceeds
      }
      return;
    }

    setForm({ ...form, quantity: "" }); 
    fetchTrades(); // Refresh trades list
    setSymbolError(""); 
  };

  const handleBuy = () => validateAndExecuteTrade("buy");
  const handleSell = () => validateAndExecuteTrade("sell");

  const handleLogout = async () => {
    if (!user || !user.id) return; // Should not happen if user is properly passed
    console.log("Logging out user:", user.id);
    const { error } = await supabase.auth.signOut();
    if (error) {
        console.error("Error logging out:", error.message);
    } else {
        // Capital is associated with user.id, so it should ideally be cleared
        // or handled based on application logic when a user logs out.
        // For now, let App.js handle redirecting to AuthForm which doesn't use this user's capital.
        // localStorage.removeItem("capital_" + user.id); // This would clear it
        // window.location.reload(); // App.js will detect no user and show AuthForm
        // Let onAuthStateChange in App.js handle the UI update.
    }
  };

  const calculatePnL = (currentTrades) => {
    const summary = {};
    currentTrades.forEach(({ symbol, quantity, price, type }) => {
      const s = symbol.toUpperCase();
      if (!summary[s]) {
        summary[s] = { buyQty: 0, buyTotal: 0, sellQty: 0, sellTotal: 0, netQty: 0 };
      }
      const q = Number(quantity);
      const p = Number(price);

      if (type === "buy") {
        summary[s].buyQty += q;
        summary[s].buyTotal += q * p;
        summary[s].netQty += q;
      } else { 
        summary[s].sellQty += q;
        summary[s].sellTotal += q * p;
        summary[s].netQty -= q;
      }
    });

    return Object.entries(summary).map(([symbol, data]) => {
      const avgBuy = data.buyQty > 0 ? data.buyTotal / data.buyQty : 0;
      const costOfSoldShares = avgBuy * data.sellQty;
      const profit = data.sellTotal - costOfSoldShares;

      return {
        symbol,
        netQty: data.netQty,
        avgBuyPrice: avgBuy.toFixed(2),
        profit: profit.toFixed(2), 
      };
    });
  };

  const pnlData = calculatePnL(trades);

  return (
    <div style={{ padding: "1rem 2rem", fontFamily: "Arial", maxWidth: 900, margin: "auto" }}>
      <h1>üìà Paper Trading Dashboard (US Market)</h1>
      { user && user.email && <p>Logged in as: {user.email} (ID: {user.id})</p> } {/* Display user.id for debugging */}
      <p>Capital: {CURRENCY_SYMBOL}{capital.toFixed(2)}</p>
      {user && <button onClick={handleLogout} style={{marginBottom: "1rem"}}>Logout</button>} {/* Show logout only if user exists */}

      {isInvalidApiKey(FINNHUB_API_KEY) && (
        <p style={{ color: "red", fontWeight: "bold", border: "1px solid red", padding: "0.5rem", margin: "1rem 0" }}>
          WARNING: A valid Finnhub API Key is not set in `TradingDashboard.js`. 
          Symbol loading, live prices, and trading will not work correctly.
        </p>
      )}

      <form style={{ marginBottom: "1.5rem", display: "flex", gap: "0.5rem", flexWrap: "wrap", alignItems: "center" }}>
        <input
          name="symbol"
          placeholder="Symbol (e.g., AAPL)"
          value={form.symbol}
          onChange={handleChange}
          required
          style={{ width: "180px", textTransform: "uppercase", padding: "8px", border: "1px solid #ccc" }}
          list="symbols-list"
          title="Enter US stock symbol, e.g., AAPL, MSFT"
        />
        <datalist id="symbols-list">
            {availableSymbols.map((sym) => (
                <option key={sym} value={sym} />
            ))}
        </datalist>

        <input
          name="quantity"
          type="number"
          placeholder="Quantity"
          value={form.quantity}
          onChange={(e) => setForm({ ...form, quantity: e.target.value })}
          required
          min="1"
          style={{ width: "100px", padding: "8px", border: "1px solid #ccc" }}
        />
        <button type="button" onClick={handleBuy} style={{padding: "8px 15px", backgroundColor: "#4CAF50", color: "white", border: "none", cursor: "pointer", borderRadius: "4px" }}>Buy</button>
        <button type="button" onClick={handleSell} style={{padding: "8px 15px", backgroundColor: "#f44336", color: "white", border: "none", cursor: "pointer", borderRadius: "4px" }}>Sell</button>
      </form>

      {symbolError && <p style={{ color: "red", marginTop: "0.5rem", padding: "0.5rem", border: "1px dashed red" }}>‚ö†Ô∏è {symbolError}</p>}

      <h2>Live Prices (US Stocks)</h2>
      {Object.keys(livePrices).filter(sym => livePrices[sym] !== null && typeof livePrices[sym] === 'number').length > 0 ? (
        <ul style={{ listStyleType: "none", paddingLeft: 0, columns: 2, columnGap: "20px" }}>
          {Object.entries(livePrices).map(([sym, price]) =>
             (price !== null && typeof price === 'number') ? ( 
              <li key={sym} style={{ padding: "0.2rem 0", borderBottom: "1px solid #eee" }}>{sym}: {CURRENCY_SYMBOL}{price.toFixed(2)}</li>
            ) : null
          )}
        </ul>
      ) : (
        <p>No live prices for US stocks currently displayed. Ensure your API key is valid and type a valid US ticker (e.g. AAPL).</p>
      )}

      <h2>Trade History</h2>
      {trades.length > 0 ? (
        <table border="1" cellPadding="8" style={{ width: "100%", borderCollapse: "collapse", marginBottom: "1.5rem" }}>
          <thead style={{backgroundColor: "#f0f0f0"}}>
            <tr>
              <th>Symbol</th>
              <th>Qty</th>
              <th>Price</th>
              <th>Type</th>
              <th>Time</th>
            </tr>
          </thead>
          <tbody>
            {trades.map((t) => (
              <tr key={t.id}>
                <td>{t.symbol}</td>
                <td>{t.quantity}</td>
                <td>{CURRENCY_SYMBOL}{Number(t.price).toFixed(2)}</td>
                <td style={{ color: t.type === "buy" ? "green" : "red", textTransform: "capitalize"}}>{t.type}</td>
                <td>{new Date(t.created_at).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (<p>No trades made yet.</p>)}

      <h2>Holdings & Profit/Loss</h2>
      {pnlData.length > 0 ? ( 
        <table border="1" cellPadding="8" style={{ width: "100%", borderCollapse: "collapse", marginBottom: "1.5rem" }}>
          <thead style={{backgroundColor: "#f0f0f0"}}>
            <tr>
              <th>Symbol</th>
              <th>Net Qty Held</th>
              <th>Avg. Buy Price</th>
              <th>Realized P&L</th>
            </tr>
          </thead>
          <tbody>
            {pnlData.map((row) => (
              <tr key={row.symbol}>
                <td>{row.symbol}</td>
                <td>{row.netQty}</td>
                <td>{CURRENCY_SYMBOL}{row.avgBuyPrice}</td>
                <td style={{ color: parseFloat(row.profit) >= 0 ? "green" : "red" }}>{CURRENCY_SYMBOL}{row.profit}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (<p>No current holdings or realized P&L to display.</p>)}

      <h2>P&L Chart (Realized)</h2>
      {pnlData.filter(p => parseFloat(p.profit) !== 0).length > 0 ? (
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={pnlData.filter(p => parseFloat(p.profit) !== 0)}> 
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="symbol" />
            <YAxis tickFormatter={(value) => `${CURRENCY_SYMBOL}${value}`}/>
            <Tooltip formatter={(value) => `${CURRENCY_SYMBOL}${Number(value).toFixed(2)}`} />
            <Legend />
            <Bar dataKey="profit" name="Realized P&L">
              {pnlData.filter(p => parseFloat(p.profit) !== 0).map((entry, index) => (
                <Cell key={`cell-${index}`} fill={parseFloat(entry.profit) >= 0 ? "#4caf50" : "#f44336"} />
              ))}
            </Bar>
          </BarChart>
        </ResponsiveContainer>
      ) : (<p>No realized P&L to chart.</p>)}
    </div>
  );
}

export default TradingDashboard;
