import React, { useState, useEffect } from "react";
import axios from "axios";
import { supabase } from "./supabaseClient"; // Make sure you configured this file
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  Cell,
} from "recharts";

const API_KEY = "d0uaoehr01qn5fk47mdgd0uaoehr01qn5fk47me0";

function App() {
  const [user, setUser] = useState(null);

  // Auth form states
  const [authEmail, setAuthEmail] = useState("");
  const [authPassword, setAuthPassword] = useState("");
  const [isSigningUp, setIsSigningUp] = useState(false);

  // Trading app states
  const [trades, setTrades] = useState([]);
  const [livePrices, setLivePrices] = useState({});
  const [form, setForm] = useState({ symbol: "", quantity: "", type: "buy" });
  const [capital, setCapital] = useState(() => {
    // Try to load capital from localStorage (per user)
    const saved = localStorage.getItem("capital");
    return saved ? Number(saved) : 100000;
  });

  // --- AUTH HANDLERS ---
  useEffect(() => {
    const session = supabase.auth.getSession().then(({ data }) => {
      setUser(data.session?.user ?? null);
    });

    const { data: listener } = supabase.auth.onAuthStateChange(
      (_event, session) => {
        setUser(session?.user ?? null);
      }
    );

    return () => {
      listener?.subscription.unsubscribe();
    };
  }, []);

  const handleSignup = async () => {
    const { error } = await supabase.auth.signUp({
      email: authEmail,
      password: authPassword,
    });
    if (error) alert("Signup error: " + error.message);
    else alert("Signup successful! Please check your email for confirmation.");
  };

  const handleLogin = async () => {
    const { error } = await supabase.auth.signInWithPassword({
      email: authEmail,
      password: authPassword,
    });
    if (error) alert("Login error: " + error.message);
  };

  const handleLogout = async () => {
    await supabase.auth.signOut();
    setUser(null);
  };

  // --- TRADING APP LOGIC ---
  useEffect(() => {
    if (!user) return;
    fetchTrades();
  }, [user]);

  const fetchTrades = async () => {
    const { data, error } = await supabase
      .from("trades")
      .select("*")
      .eq("user_id", user.id)
      .order("created_at", { ascending: true });
    if (!error) setTrades(data);
  };

  // Fetch live prices every 10s for unique symbols in trades + form.symbol if new
  useEffect(() => {
    if (!user) return;

    const symbols = [
      ...new Set(trades.map((t) => t.symbol.toUpperCase()).concat([form.symbol.toUpperCase()])),
    ].filter((s) => s); // filter empty

    const fetchPrices = async () => {
      const prices = {};
      for (const sym of symbols) {
        try {
          const res = await axios.get(
            `https://finnhub.io/api/v1/quote?symbol=${sym}.NS&token=${API_KEY}`
          );
          prices[sym] = res.data.c;
        } catch (err) {
          console.error("Price fetch error for", sym, err.message);
        }
      }
      setLivePrices(prices);
    };

    fetchPrices();
    const interval = setInterval(fetchPrices, 10000);
    return () => clearInterval(interval);
  }, [trades, form.symbol, user]);

  // Save capital to localStorage on change (simple persistence)
  useEffect(() => {
    localStorage.setItem("capital", capital);
  }, [capital]);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value.toUpperCase() });

  // Add manual trade - if you want to allow manual price override (optional)
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.symbol || !form.quantity) {
      alert("Symbol and quantity required");
      return;
    }
    const tradeData = {
      user_id: user.id,
      symbol: form.symbol.toUpperCase(),
      quantity: Number(form.quantity),
      price: Number(form.price),
      type: form.type,
    };
    if (!tradeData.price || tradeData.price <= 0) {
      alert("Please enter valid price");
      return;
    }
    const { error } = await supabase.from("trades").insert([tradeData]);
    if (error) {
      alert("Error adding trade: " + error.message);
      return;
    }
    setForm({ symbol: "", quantity: "", price: "", type: "buy" });
    fetchTrades();
  };

  // Buy at live price
  const handleBuy = async () => {
    if (!form.symbol || !form.quantity) {
      alert("Please enter symbol and quantity");
      return;
    }
    const sym = form.symbol.toUpperCase();
    const qty = Number(form.quantity);
    const price = livePrices[sym];
    if (!price) {
      alert("Live price not available for " + sym);
      return;
    }
    const cost = qty * price;
    if (cost > capital) {
      alert("❌ Not enough capital!");
      return;
    }

    const trade = {
      user_id: user.id,
      symbol: sym,
      quantity: qty,
      price: price,
      type: "buy",
    };
    const { error } = await supabase.from("trades").insert([trade]);
    if (error) {
      alert("Error inserting trade: " + error.message);
      return;
    }
    setCapital((c) => c - cost);
    setForm({ ...form, symbol: sym, quantity: "", price: "" });
    fetchTrades();
  };

  // Sell at live price
  const handleSell = async () => {
    if (!form.symbol || !form.quantity) {
      alert("Please enter symbol and quantity");
      return;
    }
    const sym = form.symbol.toUpperCase();
    const qty = Number(form.quantity);
    const price = livePrices[sym];
    if (!price) {
      alert("Live price not available for " + sym);
      return;
    }

    const trade = {
      user_id: user.id,
      symbol: sym,
      quantity: qty,
      price: price,
      type: "sell",
    };
    const { error } = await supabase.from("trades").insert([trade]);
    if (error) {
      alert("Error inserting trade: " + error.message);
      return;
    }
    setCapital((c) => c + qty * price);
    setForm({ ...form, symbol: sym, quantity: "", price: "" });
    fetchTrades();
  };

  // Calculate PnL summary per symbol
  const calculatePnL = (trades) => {
    const summary = {};
    trades.forEach(({ symbol, quantity, price, type }) => {
      if (!summary[symbol]) {
        summary[symbol] = {
          totalBuyQty: 0,
          totalBuyCost: 0,
          totalSellQty: 0,
          totalSellRevenue: 0,
        };
      }
      const qty = Number(quantity),
        pr = Number(price);
      if (type === "buy") {
        summary[symbol].totalBuyQty += qty;
        summary[symbol].totalBuyCost += qty * pr;
      } else {
        summary[symbol].totalSellQty += qty;
        summary[symbol].totalSellRevenue += qty * pr;
      }
    });

    return Object.entries(summary).map(([symbol, data]) => {
      const avgBuy = data.totalBuyQty ? data.totalBuyCost / data.totalBuyQty : 0;
      const profit = data.totalSellRevenue - avgBuy * data.totalSellQty;
      return {
        symbol,
        netQty: data.totalBuyQty - data.totalSellQty,
        avgBuyPrice: avgBuy.toFixed(2),
        profit: profit.toFixed(2),
      };
    });
  };

  if (!user) {
    // Show login/signup form if not authenticated
    return (
      <div style={{ padding: "2rem", fontFamily: "Arial", maxWidth: 400, margin: "auto" }}>
        <h1>{isSigningUp ? "Sign Up" : "Login"}</h1>
        <input
          type="email"
          placeholder="Email"
          value={authEmail}
          onChange={(e) => setAuthEmail(e.target.value)}
          style={{ width: "100%", marginBottom: "0.5rem", padding: "0.5rem" }}
        />
        <input
          type="password"
          placeholder="Password"
          value={authPassword}
          onChange={(e) => setAuthPassword(e.target.value)}
          style={{ width: "100%", marginBottom: "0.5rem", padding: "0.5rem" }}
        />
        {isSigningUp ? (
          <>
            <button onClick={handleSignup} style={{ width: "100%", padding: "0.5rem" }}>
              Sign Up
            </button>
            <p>
              Already have an account?{" "}
              <button onClick={() => setIsSigningUp(false)} style={{ color: "blue", cursor: "pointer", background: "none", border: "none" }}>
                Login
              </button>
            </p>
          </>
        ) : (
          <>
            <button onClick={handleLogin} style={{ width: "100%", padding: "0.5rem" }}>
              Login
            </button>
            <p>
              Don't have an account?{" "}
              <button onClick={() => setIsSigningUp(true)} style={{ color: "blue", cursor: "pointer", background: "none", border: "none" }}>
                Sign Up
              </button>
            </p>
          </>
        )}
      </div>
    );
  }

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial", maxWidth: 900, margin: "auto" }}>
      <h1>📊 Paper Trading App</h1>
      <h2>💼 Capital: ₹{capital.toFixed(2)}</h2>
      <button onClick={handleLogout} style={{ marginBottom: "1rem" }}>
        Logout
      </button>

      <form
        onSubmit={handleSubmit}
        style={{ display: "flex", gap: "0.5rem", flexWrap: "wrap", alignItems: "center", marginBottom: "1rem" }}
      >
        <input
          name="symbol"
          placeholder="Symbol"
          value={form.symbol}
          onChange={handleChange}
          required
          style={{ width: "6rem", textTransform: "uppercase" }}
        />
        <input
          name="quantity"
          type="number"
          min="1"
          placeholder="Quantity"
          value={form.quantity}
          onChange={(e) => setForm({ ...form, quantity: e.target.value })}
          required
          style={{ width: "6rem" }}
        />
        {/* Price input optional - remove if you want */}
        <input
          name="price"
          type="number"
          min="0"
          step="0.01"
          placeholder="Price (optional)"
          value={form.price || ""}
          onChange={(e) => setForm({ ...form, price: e.target.value })}
          style={{ width: "8rem" }}
        />
        <select name="type" value={form.type} onChange={handleChange} style={{ width: "6rem" }}>
          <option value="buy">Buy</option>
          <option value="sell">Sell</option>
        </select>
        <button type="submit" style={{ padding: "0.5rem 1rem" }}>
          💾 Add Trade
        </button>
      </form>

      <div style={{ marginBottom: "1rem" }}>
        <button onClick={handleBuy} style={{ marginRight: "1rem" }}>
          🟢 Buy at Live Price
        </button>
        <button onClick={handleSell}>🔴 Sell at Live Price</button>
      </div>

      <h2>📈 Live Prices</h2>
      <ul>
        {Object.entries(livePrices).map(([sym, price]) => (
          <li key={sym}>
            <strong>{sym}</strong>: ₹{price?.toFixed(2) ?? "-"}
          </li>
        ))}
      </ul>

      <h2>📜 Trade History</h2>
      <table border="1" cellPadding="8" style={{ width: "100%", marginBottom: "2rem", borderCollapse: "collapse" }}>
        <thead>
          <tr style={{ backgroundColor: "#eee" }}>
            <th>Symbol</th>
            <th>Qty</th>
            <th>Price</th>
            <th>Type</th>
            <th>Time</th>
          </tr>
        </thead>
        <tbody>
          {trades.map((t) => (
            <tr key={t.id}>
              <td>{t.symbol}</td>
              <td>{t.quantity}</td>
              <td>₹{Number(t.price).toFixed(2)}</td>
              <td>{t.type}</td>
              <td>{new Date(t.created_at).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>📊 Profit & Loss</h2>
      <table border="1" cellPadding="8" style={{ width: "100%", borderCollapse: "collapse", marginBottom: "2rem" }}>
        <thead>
          <tr style={{ backgroundColor: "#eee" }}>
            <th>Symbol</th>
            <th>Net Qty</th>
            <th>Avg Buy</th>
            <th>P&L</th>
          </tr>
        </thead>
        <tbody>
          {calculatePnL(trades).map((row) => (
            <tr key={row.symbol}>
              <td>{row.symbol}</td>
              <td>{row.netQty}</td>
              <td>₹{row.avgBuyPrice}</td>
              <td style={{ color: row.profit >= 0 ? "green" : "red" }}>₹{row.profit}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>📊 P&L Chart</h2>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={calculatePnL(trades)}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="symbol" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="profit">
            {calculatePnL(trades).map((entry, index) => (
              <Cell key={`cell-${index}`} fill={entry.profit >= 0 ? "#4caf50" : "#f44336"} />
            ))}
          </Bar>
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
}

export default App;
