import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { supabase } from './supabaseClient';
import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, Cell
} from 'recharts';

function App() {
  const [trades, setTrades] = useState([]);
  const [livePrices, setLivePrices] = useState({});
  const [form, setForm] = useState({ symbol: '', quantity: '', price: '', type: 'buy' });
  const [capital, setCapital] = useState(100000); // starting capital
  const [symbol, setSymbol] = useState("TCS");

  const apiKey = 'd0uaoehr01qn5fk47mdgd0uaoehr01qn5fk47me0';

  useEffect(() => {
    const fetchTrades = async () => {
      const { data, error } = await supabase.from('trades').select('*').order('created_at', { ascending: true });
      if (!error) setTrades(data);
    };
    fetchTrades();
  }, []);

  useEffect(() => {
    const symbols = [...new Set(trades.map((trade) => trade.symbol))];
    const fetchLivePrices = async () => {
      const newPrices = {};
      for (const sym of symbols) {
        try {
          const res = await axios.get(`https://finnhub.io/api/v1/quote?symbol=${sym}.NS&token=${apiKey}`);
          newPrices[sym] = res.data.c;
        } catch (err) {
          console.error(`Error fetching price for ${sym}`, err);
        }
      }
      setLivePrices(newPrices);
    };
    fetchLivePrices();
    const interval = setInterval(fetchLivePrices, 10000);
    return () => clearInterval(interval);
  }, [trades]);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { data, error } = await supabase.from('trades').insert([form]);
    if (!error) {
      setForm({ symbol: '', quantity: '', price: '', type: 'buy' });
      const updated = await supabase.from('trades').select('*').order('created_at', { ascending: true });
      setTrades(updated.data);
    }
  };

  const handleBuy = async () => {
    const qty = Number(form.quantity);
    const currentPrice = livePrices[form.symbol];
    if (!currentPrice || !qty) return;
    const cost = qty * currentPrice;
    if (cost > capital) return alert('âŒ Not enough capital!');
    setCapital(capital - cost);

    const trade = {
      symbol: form.symbol,
      quantity: qty,
      price: currentPrice,
      type: 'buy',
      timestamp: new Date().toISOString(),
    };
    await supabase.from('trades').insert([trade]);
    setTrades([...trades, trade]);
  };

  const handleSell = async () => {
    const qty = Number(form.quantity);
    const currentPrice = livePrices[form.symbol];
    if (!currentPrice || !qty) return;
    const saleAmount = qty * currentPrice;
    setCapital(capital + saleAmount);

    const trade = {
      symbol: form.symbol,
      quantity: qty,
      price: currentPrice,
      type: 'sell',
      timestamp: new Date().toISOString(),
    };
    await supabase.from('trades').insert([trade]);
    setTrades([...trades, trade]);
  };

  const calculatePnL = (trades) => {
    const summary = {};
    trades.forEach(({ symbol, quantity, price, type }) => {
      if (!summary[symbol]) {
        summary[symbol] = { totalBuyQty: 0, totalBuyCost: 0, totalSellQty: 0, totalSellRevenue: 0 };
      }
      const qty = Number(quantity), pr = Number(price);
      if (type === 'buy') {
        summary[symbol].totalBuyQty += qty;
        summary[symbol].totalBuyCost += qty * pr;
      } else {
        summary[symbol].totalSellQty += qty;
        summary[symbol].totalSellRevenue += qty * pr;
      }
    });

    return Object.entries(summary).map(([symbol, data]) => {
      const avgBuy = data.totalBuyQty ? data.totalBuyCost / data.totalBuyQty : 0;
      const profit = data.totalSellRevenue - (avgBuy * data.totalSellQty);
      return {
        symbol,
        netQty: data.totalBuyQty - data.totalSellQty,
        avgBuyPrice: avgBuy.toFixed(2),
        profit: profit.toFixed(2),
      };
    });
  };

  return (
    <div style={{ padding: '2rem', fontFamily: 'Arial' }}>
      <h1>ðŸ“Š Paper Trading App</h1>
      <h2>ðŸ’¼ Capital: â‚¹{capital.toFixed(2)}</h2>

      <form onSubmit={handleSubmit}>
        <input name="symbol" placeholder="Symbol" value={form.symbol} onChange={handleChange} required />
        <input name="quantity" type="number" placeholder="Qty" value={form.quantity} onChange={handleChange} required />
        <input name="price" type="number" placeholder="Price" value={form.price} onChange={handleChange} required />
        <select name="type" value={form.type} onChange={handleChange}>
          <option value="buy">Buy</option>
          <option value="sell">Sell</option>
        </select>
        <button type="submit">ðŸ’¾ Add Trade</button>
      </form>

      <div style={{ marginTop: '1rem' }}>
        <button onClick={handleBuy}>ðŸŸ¢ Buy at Live Price</button>
        <button onClick={handleSell}>ðŸ”´ Sell at Live Price</button>
      </div>

      <h2>ðŸ“ˆ Live Prices</h2>
      <ul>
        {Object.entries(livePrices).map(([sym, price]) => (
          <li key={sym}><strong>{sym}</strong>: â‚¹{price?.toFixed(2)}</li>
        ))}
      </ul>

      <h2>ðŸ“œ Trade History</h2>
      <table border="1" cellPadding="8">
        <thead>
          <tr><th>Symbol</th><th>Qty</th><th>Price</th><th>Type</th><th>Time</th></tr>
        </thead>
        <tbody>
          {trades.map((t) => (
            <tr key={t.id}>
              <td>{t.symbol}</td>
              <td>{t.quantity}</td>
              <td>{t.price}</td>
              <td>{t.type}</td>
              <td>{new Date(t.created_at).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>ðŸ“Š Profit & Loss</h2>
      <table border="1" cellPadding="8">
        <thead><tr><th>Symbol</th><th>Net Qty</th><th>Avg Buy</th><th>P&L</th></tr></thead>
        <tbody>
          {calculatePnL(trades).map((row) => (
            <tr key={row.symbol}>
              <td>{row.symbol}</td>
              <td>{row.netQty}</td>
              <td>{row.avgBuyPrice}</td>
