import React from 'react'; // Add this line
import { useTradingData } from './TradingDataContext';
import { Link } from 'react-router-dom';

function PortfolioPage() {
  const { 
    capital, 
    trades, 
    livePrices, 
    calculatePnL, 
    CURRENCY_SYMBOL,
    loadingData,
    calculateTotalPortfolioValue
  } = useTradingData();

  // Recalculate P&L data for display
  const pnlData = calculatePnL(trades);

  // Filter for currently held stocks
  const currentHoldings = pnlData.filter(holding => holding.netQty > 0);

  if (loadingData) {
    return (
      <div style={{ textAlign: 'center', marginTop: '50px', fontSize: '20px' }}>
        Loading portfolio data...
      </div>
    );
  }

  return (
    <div style={{ padding: "1rem 2rem", fontFamily: "Arial", maxWidth: 900, margin: "auto", backgroundColor: '#f9f9f9', borderRadius: '8px', boxShadow: '0 2px 10px rgba(0,0,0,0.05)' }}>
      <h1 style={{ textAlign: 'center', color: '#333' }}>
        <span role="img" aria-label="briefcase">ðŸ’¼</span> My Portfolio
      </h1>
      <p style={{ fontSize: '1.2rem', fontWeight: 'bold', textAlign: 'center', color: '#007bff', marginBottom: '1.5rem' }}>
        Total Portfolio Value: {CURRENCY_SYMBOL}{calculateTotalPortfolioValue()}
      </p>

      <div style={{ textAlign: 'center', marginBottom: '1.5rem' }}>
        <Link to="/dashboard" style={{
            padding: '10px 20px',
            backgroundColor: '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            fontSize: '1rem',
            textDecoration: 'none',
            transition: 'background-color 0.2s'
        }}>
          Back to Dashboard
        </Link>
      </div>

      <h2 style={{ color: '#333', marginTop: '2rem', marginBottom: '1rem' }}>Current Holdings</h2>
      {currentHoldings.length > 0 ? (
        <table style={{ width: "100%", borderCollapse: "collapse", marginBottom: "2rem", border: '1px solid #ddd', borderRadius: '8px', overflow: 'hidden' }}>
          <thead style={{backgroundColor: "#eef", color: '#333'}}>
            <tr>
              <th style={{ padding: '12px', textAlign: 'left', borderBottom: '1px solid #ddd' }}>Symbol</th>
              <th style={{ padding: '12px', textAlign: 'left', borderBottom: '1px solid #ddd' }}>Quantity Held</th>
              <th style={{ padding: '12px', textAlign: 'left', borderBottom: '1px solid #ddd' }}>Avg. Buy Price</th>
              <th style={{ padding: '12px', textAlign: 'left', borderBottom: '1px solid #ddd' }}>Current Price</th>
              <th style={{ padding: '12px', textAlign: 'left', borderBottom: '1px solid #ddd' }}>Current Value</th>
              <th style={{ padding: '12px', textAlign: 'left', borderBottom: '1px solid #ddd' }}>Unrealized P&L</th>
            </tr>
          </thead>
          <tbody>
            {currentHoldings.map((holding) => {
              const currentPrice = livePrices[holding.symbol] || 0;
              const currentValue = holding.netQty * currentPrice;
              const avgBuyPriceNum = parseFloat(holding.avgBuyPrice);
              const unrealizedPnL = holding.netQty * (currentPrice - avgBuyPriceNum);

              return (
                <tr key={holding.symbol} style={{ backgroundColor: unrealizedPnL >= 0 ? '#e6ffe6' : '#ffe6e6' }}>
                  <td style={{ padding: '12px', borderBottom: '1px solid #eee' }}>{holding.symbol}</td>
                  <td style={{ padding: '12px', borderBottom: '1px solid #eee' }}>{holding.netQty}</td>
                  <td style={{ padding: '12px', borderBottom: '1px solid #eee' }}>{CURRENCY_SYMBOL}{avgBuyPriceNum.toFixed(2)}</td>
                  <td style={{ padding: '12px', borderBottom: '1px solid #eee' }}>
                    {currentPrice > 0 ? `${CURRENCY_SYMBOL}${currentPrice.toFixed(2)}` : 'N/A'}
                  </td>
                  <td style={{ padding: '12px', borderBottom: '1px solid #eee' }}>
                    {currentValue > 0 ? `${CURRENCY_SYMBOL}${currentValue.toFixed(2)}` : 'N/A'}
                  </td>
                  <td style={{ padding: '12px', borderBottom: '1px solid #eee', color: unrealizedPnL >= 0 ? "green" : "red", fontWeight: 'bold' }}>
                    {currentPrice > 0 ? `${CURRENCY_SYMBOL}${unrealizedPnL.toFixed(2)}` : 'N/A'}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      ) : (
        <p style={{ color: '#666', border: '1px dashed #ccc', padding: '1rem', borderRadius: '5px', backgroundColor: '#fff' }}>No stocks currently held in your portfolio.</p>
      )}

      <h2 style={{ color: '#333', marginTop: '2rem', marginBottom: '1rem' }}>Capital Available</h2>
      <p style={{ fontSize: '1.1rem', fontWeight: 'bold', color: '#28a745', border: '1px solid #eee', padding: '1rem', borderRadius: '5px', backgroundColor: '#fff' }}>
        Cash: {CURRENCY_SYMBOL}{capital.toFixed(2)}
      </p>




    </div>
  );
}

export default PortfolioPage;
