import React, { useState, useEffect } from "react";
import axios from "axios";
import { supabase } from "./supabaseClient";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  Cell,
} from "recharts";

// Replace with your actual Finnhub API Key after getting it from finnhub.io
const FINNHUB_API_KEY = "d0uv0tpr01qmg3uj77qgd0uv0tpr01qmg3uj77r0"; // <--- IMPORTANT: Replace this!

function TradingDashboard({ user }) {
  const [trades, setTrades] = useState([]);
  const [livePrices, setLivePrices] = useState({});
  const [form, setForm] = useState({ symbol: "", quantity: "", type: "buy" });
  const [capital, setCapital] = useState(() => {
    const saved = localStorage.getItem("capital_" + user.id);
    return saved ? Number(saved) : 100000;
  });
  const [availableSymbols, setAvailableSymbols] = useState([]); // To store symbols from Finnhub
  const [symbolError, setSymbolError] = useState(""); // To display symbol-related errors

  // Fetch available symbols from Finnhub on component mount
  useEffect(() => {
    const fetchAvailableSymbols = async () => {
      try {
        // Fetching NSE symbols. You might want to combine NSE and BSE if needed.
        const res = await axios.get(
          `https://finnhub.io/api/v1/stock/symbol?exchange=NSE&token=${FINNHUB_API_KEY}`
        );
        // Filter for common stock types or sort as needed
        const symbols = res.data
          .filter(
            (s) =>
              s.type === "Common Stock" &&
              s.displaySymbol &&
              s.symbol.includes(".NS") // Often Finnhub symbols for NSE include .NS
          )
          .map((s) => s.displaySymbol); // Use displaySymbol for user readability
        setAvailableSymbols(symbols);
      } catch (err) {
        console.error("Error fetching available symbols:", err.message);
        setSymbolError(
          "Could not fetch available symbols. Please check your API key and network."
        );
      }
    };
    fetchAvailableSymbols();
  }, []); // Run only once on mount

  useEffect(() => {
    fetchTrades();
  }, []);

  useEffect(() => {
    // Only fetch prices for symbols present in trades or the form, and only if a valid API key exists
    if (!FINNHUB_API_KEY || FINNHUB_API_KEY === "YOUR_FINNHUB_API_KEY") {
      setSymbolError("Please set a valid Finnhub API Key.");
      return;
    }

    const symbolsToFetch = [
      ...new Set(trades.map((t) => t.symbol.toUpperCase()).concat([form.symbol.toUpperCase()])),
    ].filter((s) => s); // Filter out empty strings

    const fetchPrices = async () => {
      const prices = {};
      for (const sym of symbolsToFetch) {
        try {
          // Use the exact symbol as provided by Finnhub, which might include .NS
          // If you fetched availableSymbols, you could ensure the sym is in that list.
          const res = await axios.get(
            `https://finnhub.io/api/v1/quote?symbol=${sym}&token=${FINNHUB_API_KEY}`
          );
          if (res.data && res.data.c) {
            prices[sym] = res.data.c;
            setSymbolError(""); // Clear any previous symbol errors if a price is fetched
          } else {
            console.warn(`No valid price data for ${sym}. Check symbol and API key.`);
            setSymbolError(`No live price for ${sym}. Check symbol or API limits.`);
          }
        } catch (err) {
          console.error("Error fetching price for", sym, err.message);
          setSymbolError(`Failed to fetch price for ${sym}: ${err.message}.`);
        }
      }
      setLivePrices((prevPrices) => ({ ...prevPrices, ...prices })); // Merge new prices
    };

    fetchPrices();
    const interval = setInterval(fetchPrices, 10000);
    return () => clearInterval(interval);
  }, [trades, form.symbol, FINNHUB_API_KEY]); // Depend on API_KEY as well

  useEffect(() => {
    localStorage.setItem("capital_" + user.id, capital);
  }, [capital, user.id]);

  const fetchTrades = async () => {
    const { data, error } = await supabase
      .from("trades")
      .select("*")
      .eq("user_id", user.id)
      .order("created_at", { ascending: true });

    if (!error) setTrades(data);
    else console.error("Error fetching trades:", error.message);
  };

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value.toUpperCase() });

  const handleBuy = async () => {
    const sym = form.symbol.toUpperCase();
    const qty = Number(form.quantity);
    const price = livePrices[sym];

    if (!FINNHUB_API_KEY || FINNHUB_API_KEY === "YOUR_FINNHUB_API_KEY") {
        alert("Please set a valid Finnhub API Key to perform trades.");
        return;
    }

    if (!sym || !qty || qty <= 0 || !price) {
      alert("Invalid input or live price unavailable for " + sym);
      return;
    }

    if (!availableSymbols.includes(sym) && !sym.endsWith(".NS")) { // Basic check, better to use API validation
        alert(`Symbol ${sym} might not be a valid Indian stock on Finnhub.`);
        // Consider a more robust check if `availableSymbols` is fully populated
    }

    const cost = qty * price;
    if (cost > capital) return alert("Not enough capital");

    const trade = { user_id: user.id, symbol: sym, quantity: qty, price, type: "buy" };
    const { error } = await supabase.from("trades").insert([trade]);
    if (error) return alert("Error: " + error.message);

    setCapital((c) => c - cost);
    setForm({ ...form, quantity: "" });
    fetchTrades();
  };

  const handleSell = async () => {
    const sym = form.symbol.toUpperCase();
    const qty = Number(form.quantity);
    const price = livePrices[sym];

    if (!FINNHUB_API_KEY || FINNHUB_API_KEY === "YOUR_FINNHUB_API_KEY") {
        alert("Please set a valid Finnhub API Key to perform trades.");
        return;
    }

    if (!sym || !qty || qty <= 0 || !price) {
      alert("Invalid input or live price unavailable for " + sym);
      return;
    }

    // Basic check for net quantity owned before selling
    const pnlSummary = calculatePnL(trades);
    const heldStock = pnlSummary.find(s => s.symbol === sym);
    if (!heldStock || heldStock.netQty < qty) {
        alert(`You only hold ${heldStock ? heldStock.netQty : 0} of ${sym}. Cannot sell ${qty}.`);
        return;
    }


    const trade = { user_id: user.id, symbol: sym, quantity: qty, price, type: "sell" };
    const { error } = await supabase.from("trades").insert([trade]);
    if (error) return alert("Error: " + error.message);

    setCapital((c) => c + qty * price);
    setForm({ ...form, quantity: "" });
    fetchTrades();
  };

  const handleLogout = async () => {
    await supabase.auth.signOut();
    window.location.reload();
  };

  const calculatePnL = (trades) => {
    const summary = {};
    trades.forEach(({ symbol, quantity, price, type }) => {
      if (!summary[symbol]) {
        summary[symbol] = { buyQty: 0, buyTotal: 0, sellQty: 0, sellTotal: 0 };
      }
      const q = Number(quantity), p = Number(price);
      if (type === "buy") {
        summary[symbol].buyQty += q;
        summary[symbol].buyTotal += q * p;
      } else {
        summary[symbol].sellQty += q;
        summary[symbol].sellTotal += q * p;
      }
    });

    return Object.entries(summary).map(([symbol, { buyQty, buyTotal, sellQty, sellTotal }]) => {
      const avgBuy = buyQty ? buyTotal / buyQty : 0;
      const profit = sellTotal - avgBuy * sellQty;
      return {
        symbol,
        netQty: buyQty - sellQty,
        avgBuyPrice: avgBuy.toFixed(2),
        profit: profit.toFixed(2),
      };
    });
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial", maxWidth: 900, margin: "auto" }}>
      <h1>ðŸ“ˆ Paper Trading Dashboard</h1>
      <p>Capital: â‚¹{capital.toFixed(2)}</p>
      <button onClick={handleLogout}>Logout</button>

      <form style={{ marginTop: "1rem", display: "flex", gap: "0.5rem", flexWrap: "wrap" }}>
        <input
          name="symbol"
          placeholder="Symbol (e.g., RELIANCE.NS)"
          value={form.symbol}
          onChange={handleChange}
          required
          style={{ width: "150px", textTransform: "uppercase" }}
          // Example of using datalist for suggested symbols
          list="symbols-list"
        />
        <datalist id="symbols-list">
            {availableSymbols.map((sym) => (
                <option key={sym} value={sym} />
            ))}
        </datalist>

        <input
          name="quantity"
          type="number"
          placeholder="Qty"
          value={form.quantity}
          onChange={(e) => setForm({ ...form, quantity: e.target.value })}
          required
          min="1"
          style={{ width: "80px" }}
        />
        <button type="button" onClick={handleBuy}>Buy</button>
        <button type="button" onClick={handleSell}>Sell</button>
      </form>

      {symbolError && <p style={{ color: "red", marginTop: "0.5rem" }}>{symbolError}</p>}


      <h2>Live Prices</h2>
      <ul>
        {Object.entries(livePrices).map(([sym, price]) => (
          <li key={sym}>{sym}: â‚¹{price?.toFixed(2) ?? "Loading..."}</li>
        ))}
      </ul>

      <h2>Trade History</h2>
      <table border="1" cellPadding="8" style={{ width: "100%" }}>
        <thead>
          <tr>
            <th>Symbol</th>
            <th>Qty</th>
            <th>Price</th>
            <th>Type</th>
            <th>Time</th>
          </tr>
        </thead>
        <tbody>
          {trades.map((t) => (
            <tr key={t.id}>
              <td>{t.symbol}</td>
              <td>{t.quantity}</td>
              <td>â‚¹{Number(t.price).toFixed(2)}</td>
              <td>{t.type}</td>
              <td>{new Date(t.created_at).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Profit & Loss</h2>
      <table border="1" cellPadding="8" style={{ width: "100%" }}>
        <thead>
          <tr>
            <th>Symbol</th>
            <th>Net Qty</th>
            <th>Avg Buy</th>
            <th>P&L</th>
          </tr>
        </thead>
        <tbody>
          {calculatePnL(trades).map((row) => (
            <tr key={row.symbol}>
              <td>{row.symbol}</td>
              <td>{row.netQty}</td>
              <td>â‚¹{row.avgBuyPrice}</td>
              <td style={{ color: row.profit >= 0 ? "green" : "red" }}>â‚¹{row.profit}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>P&L Chart</h2>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={calculatePnL(trades)}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="symbol" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="profit">
            {calculatePnL(trades).map((entry, index) => (
              <Cell key={`cell-${index}`} fill={entry.profit >= 0 ? "#4caf50" : "#f44336"} />
            ))}
          </Bar>
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
}

export default TradingDashboard;
